
public class Final {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 10; // 변수
		final int num2 = 20; // 상수
		final double PI = 3.14; // 원주율처럼 바뀔 필요가 없는 값 (final 이용)
		
	
		num = 5;  
		// num2 = 5;  num2는 상수이기때문에 변경불가. 에러
		
		
		
	}
}
